import React from 'react'

export const About = () => {
  return (
    <section className="about" id="about">
      <h2>Events</h2>
      <p>Over the span of the year, we conduct variety of events and seminars to spread awareness about our cause. Along with that, we also, move to make our environment a better place with these initiatives.</p>
      <Container>
        <Row>
            {
              events.map((event, index) => {
                return (
                  <EventCard
                    key={index}
                    {...event}
                        />
                        )})
            }
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
