import React from 'react'
import BrandNavbar from '../components/BrandNavbar'
import '../styles/volApply.css'
import { Button, Form } from "react-bootstrap";
import emailjs from 'emailjs-com'


function VolApply() {
  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_gjoo1rj', 'template_rvkzwxp', e.target, 'cYvhgevJQFAGcB7Uc').then(function(response) {
          console.log('SUCCESS!', response.status, response.text);
      }, function(error) {
          console.log('FAILED...', error);
      });;
  }
  return (
    <div>
      <BrandNavbar />
      <section className='volApply'>
      </section>
      <section className='form'>
        <h2>Fill the given below form to showcase you interest in the volunteer program.</h2>
        <section>
        <form onSubmit={handleSubmit}>
                  <Row>
                    <Col size={12} sm={6} className="px-1">
                      <input type="text" value={formDetails.firstName} placeholder="First Name" onChange={(e) => onFormUpdate('firstName', e.target.value)} />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="text" value={formDetails.lasttName} placeholder="Last Name" onChange={(e) => onFormUpdate('lastName', e.target.value)}/>
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="email" value={formDetails.email} placeholder="Email Address" onChange={(e) => onFormUpdate('email', e.target.value)} />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input type="tel" value={formDetails.phone} placeholder="Phone No." onChange={(e) => onFormUpdate('phone', e.target.value)}/>
                    </Col>
                    <Col size={12} className="px-1">
                      <textarea rows="6" value={formDetails.message} placeholder="Message" onChange={(e) => onFormUpdate('message', e.target.value)}></textarea>
                      <button type="submit"><span>{buttonText}</span></button>
                    </Col>
                  </Row>
                </form>

        </section>
      </section>
    </div>
  )
}

export default VolApply
